const mainTweet = $('Loop Over Threads').first().json;

// Get the input data which contains the tweets array
const inputData = $input.first().json;

// Extract the tweets array from the nested structure
const threadTweets = inputData.tweets || [];

// Reverse to get chronological order (oldest to newest)
const orderedThreadTweets = threadTweets.reverse();

// Extract just the text from each thread tweet
const threadTexts = orderedThreadTweets.map(tweet => tweet.text);

// Build the combined text starting with main tweet
let combinedText = mainTweet.tweet_text;

// Add each thread part with numbering starting from (2)
threadTexts.forEach((text, index) => {
  combinedText += `\n\n(${index + 2}) ${text}`;
});

// Create the result maintaining the exact JSON structure from "Loop Over Threads"
const result = {
  ...mainTweet, // Keep all original fields exactly as they were
  tweet_text: combinedText // Update only the tweet_text field
};

return [result];

// Get the input data
const inputData = $input.all();
const results = [];

inputData.forEach(item => {
  const data = item.json;

  // Parse the created_at date
  const createdAt = new Date(data.created_at);

  // Format the since time (set seconds to 00)
  const sinceYear = createdAt.getUTCFullYear();
  const sinceMonth = String(createdAt.getUTCMonth() + 1).padStart(2, '0');
  const sinceDay = String(createdAt.getUTCDate()).padStart(2, '0');
  const sinceHour = String(createdAt.getUTCHours()).padStart(2, '0');
  const sinceMinute = String(createdAt.getUTCMinutes()).padStart(2, '0');

  const sinceTime = `${sinceYear}-${sinceMonth}-${sinceDay}_${sinceHour}:${sinceMinute}:00_UTC`;

  // Calculate until time (10 minutes later)
  const untilDate = new Date(createdAt.getTime() + 10 * 60 * 1000); // Add 10 minutes
  const untilYear = untilDate.getUTCFullYear();
  const untilMonth = String(untilDate.getUTCMonth() + 1).padStart(2, '0');
  const untilDay = String(untilDate.getUTCDate()).padStart(2, '0');
  const untilHour = String(untilDate.getUTCHours()).padStart(2, '0');
  const untilMinute = String(untilDate.getUTCMinutes()).padStart(2, '0');

  const untilTime = `${untilYear}-${untilMonth}-${untilDay}_${untilHour}:${untilMinute}:00_UTC`;

  // Construct the query
  const query = `since:${sinceTime} until:${untilTime} from:${data.author_username} filter:self_threads`;

  // Return the result
  results.push({
    query: query,
    original_tweet_id: data.tweet_id,
    author_username: data.author_username,
    created_at: data.created_at,
    since_time: sinceTime,
    until_time: untilTime
  });
});

return results;
