// Create array of topics
const topics = ["n8n", "AI", "defAI"];

// Return as array of objects
return topics.map(topic => ({
  json: {
    topic: topic
  }
}));

// Engagement Score Calculator with Freshness Factor (0-100 scale)
const tweets = $input.all();
const scoredTweets = [];

// Get all tweets for normalization
const allTweets = tweets.map(item => item.json);
const viewCounts = allTweets.map(t => parseInt(t.view_count) || 1);
const followerCounts = allTweets.map(t => parseInt(t.author_followers) || 1);

const maxViews = Math.max(...viewCounts);
const maxFollowers = Math.max(...followerCounts);

for (const item of tweets) {
    const tweet = item.json;

    // Parse numeric values
    const likes = parseInt(tweet.like_count) || 0;
    const retweets = parseInt(tweet.retweet_count) || 0;
    const bookmarks = parseInt(tweet.bookmark_count) || 0;
    const views = parseInt(tweet.view_count) || 1;
    const quotes = parseInt(tweet.quote_count) || 0;
    const followers = parseInt(tweet.author_followers) || 1;

    // FRESHNESS CALCULATION
    const now = new Date();
    const tweetDate = new Date(tweet.created_at);
    const hoursOld = (now - tweetDate) / (1000 * 60 * 60);
    const daysOld = hoursOld / 24;

    // Freshness multiplier (decays over time)
    let freshnessMultiplier = 1.0;

    if (daysOld <= 1) {
        freshnessMultiplier = 1.2;      // 20% boost for content < 1 day
    } else if (daysOld <= 3) {
        freshnessMultiplier = 1.1;      // 10% boost for content < 3 days
    } else if (daysOld <= 7) {
        freshnessMultiplier = 1.0;      // No change for content < 1 week
    } else if (daysOld <= 14) {
          freshnessMultiplier = 0.8;      // 20% penalty for content < 2 weeks
    } else if (daysOld <= 30) {
        freshnessMultiplier = 0.6;      // 40% penalty for content < 1 month
    } else if (daysOld <= 90) {
        freshnessMultiplier = 0.4;      // 60% penalty for content < 3 months
    } else {
        freshnessMultiplier = 0.3;      // 70% penalty for content > 3 months
    }

    // Core engagement calculation
    const totalEngagements = likes + retweets + bookmarks + quotes;
    const engagementRate = (totalEngagements / views) * 100;

    // Base scoring (before freshness)
    const engagementScore = Math.min(engagementRate * 10, 40);

    const bookmarkRate = (bookmarks / views) * 100;
    const bookmarkScore = Math.min(bookmarkRate * 12.5, 25);

    const amplificationRate = ((retweets + quotes) / views) * 100;
    const amplificationScore = Math.min(amplificationRate * 10, 20);

    const authorityScore = (followers / maxFollowers) * 10;

    let mediaBonus = 0;
    if (tweet.has_media === "true") {
        mediaBonus = tweet.media_type === "video" ? 5 : 3;
    }

    // Viral bonuses
    let viralBonus = 0;
    if (likes > 1000) viralBonus = 10;
    if (likes > 5000) viralBonus = 20;
    if (likes > 10000) viralBonus = 30;

    // Base score (before freshness)
    const baseScore = engagementScore + bookmarkScore + amplificationScore + authorityScore + mediaBonus + viralBonus;

    // Apply freshness multiplier
    const finalScore = Math.min(Math.round(baseScore * freshnessMultiplier), 100);

    scoredTweets.push({
        json: {
            ...tweet,
            engagement_score: finalScore,
            days_old: Math.round(daysOld * 10) / 10,  // Round to 1 decimal
            freshness_multiplier: Math.round(freshnessMultiplier * 100) / 100,  // Round to 2 decimals
            base_score: Math.round(baseScore)  // Score before freshness
        }
    });
}

// Sort by engagement score (highest first)
scoredTweets.sort((a, b) => b.json.engagement_score - a.json.engagement_score);

return scoredTweets;

// Enhanced Analytics Code Node with Dynamic Benchmarks
const tweets = $input.all();
const enhancedTweets = [];

// First pass: Calculate all metrics
for (const item of tweets) {
    const tweet = item.json;

    // Parse numbers safely
    const likes = parseInt(tweet.like_count) || 0;
    const retweets = parseInt(tweet.retweet_count) || 0;
    const bookmarks = parseInt(tweet.bookmark_count) || 0;
    const quotes = parseInt(tweet.quote_count) || 0;
    const views = parseInt(tweet.view_count) || 1;

    // Calculate engagement metrics
    const totalEngagements = likes + retweets + bookmarks + quotes;
    const engagementRate = parseFloat(((totalEngagements / views) * 100).toFixed(2));
    const bookmarkRate = parseFloat(((bookmarks / views) * 100).toFixed(2));
    const amplificationRate = parseFloat((((retweets + quotes) / views) * 100).toFixed(2));

    // Thread detection
    const text = tweet.tweet_text;
    const threadIndicator = 
        text.includes("ðŸ§µ") ||
        text.match(/\d+\/\d+/) ||
        text.includes("Thread:") ||
        text.includes("THREAD") ||
        (tweet.conversation_id !== tweet.tweet_id)
        ? "thread" : "single";

    // Content type analysis
    let contentType = "educational";
    if (text.includes("comment") || text.includes("follow")) {
        contentType = "engagement_bait";
    } else if (text.includes("$") || text.includes("money")) {
        contentType = "monetization";
    } else if (text.includes("here's") || text.includes("setup")) {
        contentType = "tutorial";
    }

    // Call to action detection
    const hasCallToAction = /comment|follow|repost|dm|share|like this/i.test(text);

    // Viral status based on engagement score
    let viralStatus = "low";
    if (tweet.engagement_score >= 80) viralStatus = "viral";
    else if (tweet.engagement_score >= 60) viralStatus = "high";
    else if (tweet.engagement_score >= 40) viralStatus = "medium";

    enhancedTweets.push({
        json: {
            ...tweet,
            engagement_rate: engagementRate,
            bookmark_rate: bookmarkRate,
            amplification_rate: amplificationRate,
            thread_indicator: threadIndicator,
            content_type: contentType,
            call_to_action: hasCallToAction,
            viral_status: viralStatus
        }
    });
}

// Second pass: Calculate benchmarks from all tweets
const allTweets = enhancedTweets.map(item => item.json);
const engagementRates = allTweets.map(t => t.engagement_rate).sort((a, b) => a - b);
const bookmarkRates = allTweets.map(t => t.bookmark_rate).sort((a, b) => a - b);
const amplificationRates = allTweets.map(t => t.amplification_rate).sort((a, b) => a - b);

// Calculate percentiles
function getPercentile(arr, percentile) {
    const index = Math.ceil((percentile / 100) * arr.length) - 1;
    return arr[Math.max(0, index)];
}

// Create dynamic benchmarks
const benchmarks = {
    engagement_rate: {
        poor: getPercentile(engagementRates, 25),      // Bottom 25%
        average: getPercentile(engagementRates, 50),    // Median
        good: getPercentile(engagementRates, 75),       // Top 25%
        excellent: getPercentile(engagementRates, 90)   // Top 10%
    },
    bookmark_rate: {
        poor: getPercentile(bookmarkRates, 25),
        average: getPercentile(bookmarkRates, 50),
        good: getPercentile(bookmarkRates, 75),
        excellent: getPercentile(bookmarkRates, 90)
    },
    amplification_rate: {
        poor: getPercentile(amplificationRates, 25),
        average: getPercentile(amplificationRates, 50),
        good: getPercentile(amplificationRates, 75),
        excellent: getPercentile(amplificationRates, 90)
    }
};

// BENCHMARKS FOR REFERENCE:
// These are calculated dynamically from your dataset
// Example output might look like:
// engagement_rate: { poor: 1.2, average: 2.8, good: 4.5, excellent: 6.2 }
// bookmark_rate: { poor: 0.3, average: 1.1, good: 2.4, excellent: 3.8 }
// amplification_rate: { poor: 0.1, average: 0.2, good: 0.4, excellent: 0.8 }

// Third pass: Add performance classifications
const finalTweets = enhancedTweets.map(item => {
    const tweet = item.json;

    // Classify engagement rate
    let engagementClass = 'poor';
    if (tweet.engagement_rate >= benchmarks.engagement_rate.excellent) engagementClass = 'excellent';
    else if (tweet.engagement_rate >= benchmarks.engagement_rate.good) engagementClass = 'good';
    else if (tweet.engagement_rate >= benchmarks.engagement_rate.average) engagementClass = 'average';

    // Classify bookmark rate
    let bookmarkClass = 'poor';
    if (tweet.bookmark_rate >= benchmarks.bookmark_rate.excellent) bookmarkClass = 'excellent';
    else if (tweet.bookmark_rate >= benchmarks.bookmark_rate.good) bookmarkClass = 'good';
    else if (tweet.bookmark_rate >= benchmarks.bookmark_rate.average) bookmarkClass = 'average';

    // Classify amplification rate
    let amplificationClass = 'poor';
    if (tweet.amplification_rate >= benchmarks.amplification_rate.excellent) amplificationClass = 'excellent';
    else if (tweet.amplification_rate >= benchmarks.amplification_rate.good) amplificationClass = 'good';
    else if (tweet.amplification_rate >= benchmarks.amplification_rate.average) amplificationClass = 'average';

    // Overall performance score (for agent decision making)
    let overallPerformance = 'poor';
    const classScores = {
        'poor': 1,
        'average': 2,
        'good': 3,
        'excellent': 4
    };

    const avgScore = (classScores[engagementClass] + classScores[bookmarkClass] + classScores[amplificationClass]) / 3;

    if (avgScore >= 3.5) overallPerformance = 'excellent';
    else if (avgScore >= 2.5) overallPerformance = 'good';
    else if (avgScore >= 1.5) overallPerformance = 'average';

    return {
        json: {
            ...tweet,
            engagement_class: engagementClass,
            bookmark_class: bookmarkClass,
            amplification_class: amplificationClass,
            overall_performance: overallPerformance
        }
    };
});

return finalTweets;
