const items = $input.all();

items.forEach(item => {
  // Propagate all existing form fields first
  const processedItem = { ...item.json };

  // Use "Email" (capital E) to match your form field
  if (item.json.Email && item.json.Email.trim()) {
    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const email = item.json.Email.trim().toLowerCase();

    if (emailRegex.test(email)) {
      // Valid email - create hash and auto opt-in
      const crypto = require('crypto');
      processedItem.email_hash = crypto.createHash('sha256').update(email).digest('hex');
      processedItem.email_opt_in = 'Yes';

      // Keep only ONE email field (overwrite the original with cleaned version)
      processedItem.Email = email;
    } else {
      // Invalid email format
      processedItem.email_hash = null;
      processedItem.email_opt_in = 'No';
      processedItem.Email = null;
    }
  } else {
    // No email provided
    processedItem.email_hash = null;
    processedItem.email_opt_in = 'No';
    processedItem.Email = null;
  }

  // Normalize other field names for consistency
  if (item.json['Personal state']) {
    processedItem.current_emotional_state = item.json['Personal state'];
  }

  if (item.json['Life Situation/Goals']) {
    processedItem.life_situation = item.json['Life Situation/Goals'];
  }

  if (item.json['Goal']) {
    processedItem.life_situation = item.json['Goal'];
  }
  // Update the item
  item.json = processedItem;
});

return items;

