// Helper function: trims each line and joins them with actual newline characters.
function toTrimmed(str) {
  return str
    .split('\n')
    .map(line => line.trim())
    .filter(line => line !== '')
    .join('\n');
}

// Extract the header title from the "Calculation" field in the first record.
const headerTitle = items[0].json["Calculation"] || "Research Report";

// Process each input item to build content sections.
const sections = items.map(item => {
  // Build a citation map from the "Citation Individual" field.
  let citationMap = {};
  const citationsString = item.json["Citation Individual"];
  if (citationsString) {
    // Regex to capture citations in the format: "1. https://..."
    const regex = /(\d+)\.\s*(https?:\/\/\S+)/g;
    let match;
    while ((match = regex.exec(citationsString)) !== null) {
      // Remove any trailing comma from the captured URL.
      citationMap[match[1]] = match[2].replace(/,$/, '');
    }
  }

  // Process the HTML body: replace occurrences of [number] with a linked citation.
  let htmlBody = item.json["HTML body"];
  htmlBody = htmlBody.replace(/\[(\d+)\]/g, (match, p1) => {
    if (citationMap[p1]) {
      return `<a href="${citationMap[p1]}" target="_blank">[${p1}]</a>`;
    }
    return match;
  });

  // Build the section HTML using Section name, Sub title (if present), and the processed HTML body.
  let sectionHTML = `<div class="section">\n`;
  sectionHTML += `<h2>${item.json["Section name"]}</h2>\n`;
  if (item.json["Sub title"]) {
    sectionHTML += `<h3>${item.json["Sub title"]}</h3>\n`;
  }
  sectionHTML += htmlBody + '\n';
  sectionHTML += `</div>`;

  return {
    sectionHTML,
    citationMap
  };
});

// Merge all citation maps from all sections into one global citation map.
const globalCitations = {};
sections.forEach(sec => {
  for (const key in sec.citationMap) {
    globalCitations[key] = sec.citationMap[key];
  }
});

// Build the main content HTML: start with the header title from the Calculation field, then all sections.
let content = `<h1>${headerTitle}</h1>\n`;
sections.forEach(sec => {
  content += sec.sectionHTML + '\n\n';
});

// Build the References section with a page break before it and make the "References" text bigger.
let referencesHTML = `<p style="page-break-before: always; font-size: 28px;"><strong>References</strong></p>\n<div class="references">\n`;
for (const num in globalCitations) {
  referencesHTML += `<p>[${num}] <a href="${globalCitations[num]}" target="_blank">${globalCitations[num]}</a></p>\n`;
}
referencesHTML += `</div>\n`;
content += referencesHTML;

// Define the CSS with custom styling for the References section.
const cssContent = `<style>
body {
  font-family: Arial, sans-serif;
  line-height: 1.2;
  padding: 20px;
  white-space: pre-wrap; /* Ensures that newline characters are rendered */
}
h1 {
  font-size: 36px;
  color: #333;
  margin-bottom: 10px;
}
h2 {
  font-size: 22px;
  color: #777;
  margin-bottom: 10px;
}
h3 {
  font-size: 20px;
  color: #444;
  margin-bottom: 10px;
}
p {
  font-size: 16px;
  color: #222;
  line-height: 1.6;
  margin: 10px 0;
}
.section {
  margin-bottom: 30px;
  border-bottom: 1px solid #ccc;
  padding-bottom: 15px;
}
/* Custom styling for the References section */
.references {
  margin: 0;
  padding: 0;
}
.references p {
  margin: 2px 0;
  font-size: 14px;
  font-style: italic;
}
</style>`;

// Build the final output object.
const output = {
  body: toTrimmed(content),
  css: toTrimmed(cssContent),
  data: {
    name: "This is a title"
  },
  settings: {
    paper_size: "A4",
    orientation: "1",
    header_font_size: "9px",
    margin_top: "40",
    margin_right: "10",
    margin_bottom: "40",
    margin_left: "10",
    print_background: "1",
    displayHeaderFooter: true,
    // Remove header/footer content to eliminate page numbers and the date.
    custom_header: "",
    custom_footer: ""
  }
};

// Return the output as a stringified JSON so that the PDF API receives valid JSON.
return [{ json: { data: JSON.stringify(output) } }];

// Retrieve our arrays from $json
const citationsArray = $json.Citations;
const subtitlesArray = $json.sectionSubTitle;

// Convert Citations to a comma-separated string
const commaSeparatedCitations = citationsArray.join(', ');

// Get the first item from the subtitles array
const firstSubTitle = subtitlesArray[0];

// Return both in a single output item
return [
  {
    commaSeparatedCitations,
    firstSubTitle
  }
];

